mapper.select:
SELECT MAX(CAST(SUBSTRING(USER_ID, LENGTH(user_id) - LOCATE('-', REVERSE(user_id)) + 2) AS UNSIGNED))
FROM user
WHERE USER_ID like 'student2023-%';

SELECT MAX(CAST(SUBSTRING(USER_ID, LENGTH(user_id) - LOCATE('-', REVERSE(user_id)) + 2) AS UNSIGNED))
FROM user
WHERE USER_ID like 'prof2023-%';
